function CartSummaryViewModel(n){const t=this;t.cart=n;for(let n=0;n<t.cart.cartItems.length;n++){const i=t.cart.cartItems[n];i.quantity=ko.observable(i.quantity).extend({subTotal:i.book.salePrice})}t.cart.cartItems=ko.observableArray(t.cart.cartItems);t.showCart=function(){$("#cart").popover("toggle")};t.fadeIn=function(n){setTimeout(function(){$("#cart").popover("show");$(n).slideDown(function(){setTimeout(function(){$("#cart").popover("hide")},2e3)})},100)};$("#cart").popover({html:!0,content:function(){return $("#cart-summary").html()},title:"Cart Details",placement:"bottom",animation:!0,trigger:"manual"})}